import React, { useState } from 'react';
import axios from 'axios';
import './index.css';

function App() {
  const [messages, setMessages] = useState([
    { role: 'system', content: 'You are a helpful assistant.' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;
    setLoading(true);
    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    try {
      const res = await axios.post('http://localhost:5000/api/chat', { messages: newMessages });
      setMessages([...newMessages, res.data.choices[0].message]);
    } catch (err) {
      setMessages([...newMessages, { role: 'assistant', content: 'Sorry, there was an error.' }]);
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="w-full max-w-xl bg-white rounded shadow p-6 flex flex-col h-[80vh]">
        <div className="flex-1 overflow-y-auto mb-4">
          {messages.filter(m => m.role !== 'system').map((msg, i) => (
            <div key={i} className={`mb-2 ${msg.role === 'user' ? 'text-right' : 'text-left'}`}>
              <span className={`inline-block px-4 py-2 rounded ${msg.role === 'user' ? 'bg-blue-200' : 'bg-gray-200'}`}>
                {msg.content}
              </span>
            </div>
          ))}
          {loading && <div>Thinking...</div>}
        </div>
        <div className="flex">
          <input
            className="flex-1 border rounded px-3 py-2 mr-2"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && sendMessage()}
            disabled={loading}
            placeholder="Type your message..."
          />
          <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={sendMessage} disabled={loading || !input}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
